
// This is a placeholder file that will be replaced by a script that fetches and adds the PDF.js worker
// This script will execute when the page loads
(function() {
  // Create a script element to load the PDF.js worker from a CDN
  const script = document.createElement('script');
  script.src = `https://cdn.jsdelivr.net/npm/pdfjs-dist@${self.pdfjsLib ? self.pdfjsLib.version : '3.11.174'}/build/pdf.worker.min.js`;
  script.onerror = () => {
    console.error('Failed to load PDF.js worker from CDN');
    // Fallback to another CDN
    const fallbackScript = document.createElement('script');
    fallbackScript.src = 'https://unpkg.com/pdfjs-dist@3.11.174/build/pdf.worker.min.js';
    document.head.appendChild(fallbackScript);
  };
  document.head.appendChild(script);
})();
